import React from 'react';
import './App.css';




/*
const size = [9, 16];

let maze = [];

for(let i=0; i<size[0]; i++){
    maze.push([]);

    for(let j=0; j<size[1]; j++){
        maze[i][j] = [(0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random())];
    }
}
*/

function createMaze(height, width){
  let maze = [];

  for(let i=0; i<height; i++){
      maze.push([]);

      for(let j=0; j<width; j++){

          maze[i][j] = [(0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random())];



          //makes walls on corners and edges of the maze
          if(i===0){
              maze[i][j][0] = true;
          }
          if(j===0){
              maze[i][j][3] = true;
          }
          if(i===height-1){
              maze[i][j][2] = true;
          }
          if(j===width-1){
              maze[i][j][1] = true;
          }



      }
  }
  return(maze);
}


createMaze(9, 16);



function arrayToClass(array){

  let borders = "field ";

  if(array[0]){
    borders += "border-top ";
  }
  if(array[1]){
    borders += "border-right ";
  }
  if(array[2]){
    borders += "border-bottom ";
  }
  if(array[3]){
    borders += "border-left ";
  }

  return(borders);

}


class Field extends React.Component{

  render(){

    return(
      <div className={ arrayToClass(this.props.borderArray) }>
        {console.log(arrayToClass(this.props.borderArray))}
      </div>
    )

  }
}

class Row extends React.Component{
  render(){
    return(
      <div className="row">

        { this.props.row.map( field => <Field borderArray={field} /> ) }

      </div>
    )
  }
}


class Maze extends React.Component{

  render(){

    return(
      <div className="maze">

        { maze.map( row => <Row row={row} /> ) }

        {// maze.map(row => row.map( field => <Field borderArray={field} /> ) ) }
        //{//will need to give keys, not sure how to do that for a matrix, probably row + field as a string
        }

      </div>
    )

  }
}



function App() {
  return (
    <div className="App">

      <Maze/>

    </div>
  );
}

export default App;
