import React from 'react';
import './App.css';





const size = [4, 6];

let lab = [];

for(let i=0; i<size[0]; i++){
    lab.push([]);

    for(let j=0; j<size[1]; j++){
        lab[i][j] = [(0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random()), (0.5 >= Math.random())];
    }
}


function arrayToClass(array){

  let borders = "field ";

  if(array[0]){
    borders += "border-top ";
  }
  if(array[1]){
    borders += "border-right ";
  }
  if(array[2]){
    borders += "border-bottom ";
  }
  if(array[3]){
    borders += "border-left ";
  }

  return(borders);

}


class Field extends React.Component{

  render(){

    return(
      <div className={ arrayToClass(this.props.borderArray) }>
        {console.log(arrayToClass(this.props.borderArray))}
      </div>
    )

  }
}




class Lab extends React.Component{

  render(){

    return(
      <div>

        <Field borderArray={ lab[1][2] } />
        { lab.map(row => row.map( field => <Field borderArray={field} /> ) ) }
        {//will need to give keys, not sure how to do that for a matrix, probably row + field as a string
        }

      </div>
    )

  }
}



function App() {
  return (
    <div className="App">

      <Lab/>

    </div>
  );
}

export default App;
