import React from 'react';
import './App.css';














//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

function printSudoku(sudoku){
  let row = ""

  for(let i in sudoku){
      for(let j in sudoku[i]){
          row += sudoku[i][j].toString();
      }
      console.log(row);
      row = "";
  }
}


function rowDataUpdate(sudoku, field, oldData){//field wil be an array [x, y]
  let n;
  let update = [];

  for(let x = 0; x < 9; x++){
      n = sudoku[field[1]][x];
      if(n !== 0 && !(oldData.includes(n))){
          update.push(n);
      }
  }

  return(update);
}

function columnDataUpdate(sudoku, field, oldData){
  let n;
  let update = [];

  for(let y = 0; y < 9; y++){
      n = sudoku[y][field[0]];
      if(n !== 0 && !(oldData.includes(n))){
          update.push(n);
      }
  }

  return(update);
}

function squareDataUpdate(sudoku, field, oldData){

  const xr = field[0]%3;
  const yr = field[1]%3;

  let n;
  let update = [];

  n = sudoku[ field[1] - yr ][ field[0] - xr ];//row
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr ][ field[0] - xr + 1 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr ][ field[0] - xr + 2 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }

  n = sudoku[ field[1] - yr + 1 ][ field[0] - xr ];//row
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr + 1 ][ field[0] - xr + 1 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr + 1 ][ field[0] - xr + 2 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }

  n = sudoku[ field[1] - yr + 2 ][ field[0] - xr ];//row
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr + 2 ][ field[0] - xr + 1 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }
  n = sudoku[ field[1] - yr + 2 ][ field[0] - xr + 2 ];
  if(n !== 0 && !(oldData.includes(n)) && !(update.includes(n))){
      update.push(n);
  }

  return(update);
}

function update(sudoku, field, oldData){

  let update = rowDataUpdate(sudoku, field, oldData);

  update = update.concat(columnDataUpdate(sudoku, field, update.concat(oldData) ));

  update = update.concat(squareDataUpdate(sudoku, field, update.concat(oldData) ));

  return(update);
}


function returnMissing(array){//to be used on tracker
  let missing = []

  for(let i = 1; i < 10; i++){
      if(!(array.includes(i))){
          missing.push(i);
      }
  }

  return(missing);
}

function findStandard(tracker, sudoku){
  let standard = 0;//relatable

  for(let y = 0; y < 9; y++){
      for(let x = 0; x < 9; x++){

          if(sudoku[y][x] === 0){

              if(tracker["field" + x.toString() + y.toString()].length > standard && tracker["field" + x.toString() + y.toString()] !== "filled"){
                  standard = tracker["field" + x.toString() + y.toString()].length;
              }
          }
      }
  }

  if(standard === 0){
      standard = "filled";
  }

  return(standard);
}

function trackAll(sudoku, oldTracker){
  let tracker = oldTracker;

  for(let y = 0; y < 9; y++){

      for(let x = 0; x < 9; x++){

          if(sudoku[y][x] === 0){

              let news = update(sudoku, [x, y], tracker["field" + x.toString() + y.toString()]);
              tracker["field" + x.toString() + y.toString()] = tracker["field" + x.toString() + y.toString()].concat(news);

          } else{
              tracker["field" + x.toString() + y.toString()] = "filled";
          }

      }

  }

  return(tracker);
}

//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////















const sudoku = [ [0, 0, 3, 0, 2, 0, 6, 0, 0], [9, 0, 0, 3, 0, 5, 0, 0, 1], [0, 0, 1, 8, 0, 6, 4, 0, 0], [0, 0, 8, 1, 0, 2, 9, 0, 0], [7, 0, 0, 0, 0, 0, 0, 0, 8], [0, 0, 6, 7, 0, 8, 2, 0, 0], [0, 0, 2, 6, 0, 9, 5, 0, 0], [8, 0, 0, 2, 0, 3, 0, 0, 9], [0, 0, 5, 0, 1, 0, 3, 0, 0] ];



class RowOfFields extends React.Component{

  render(){

    return(
      <div className="row">

        <div className="field">{this.props.row[0]}</div>
        <div className="field">{this.props.row[1]}</div>
        <div className="field">{this.props.row[2]}</div>

      </div>
    )
  }
}


class RowOfSquares extends React.Component{

  render(){

    return(
      <div className="row">

        <div className="square"> <RowOfFields row = {[ this.props.row[0][0], this.props.row[0][1], this.props.row[0][2] ]} />
                                 <RowOfFields row = {[ this.props.row[1][0], this.props.row[1][1], this.props.row[1][2] ]} />
                                 <RowOfFields row = {[ this.props.row[2][0], this.props.row[2][1], this.props.row[2][2] ]} /> </div>

        <div className="square"> <RowOfFields row = {[ this.props.row[0][3], this.props.row[0][4], this.props.row[0][5] ]} />
                                 <RowOfFields row = {[ this.props.row[1][3], this.props.row[1][4], this.props.row[1][5] ]} />
                                 <RowOfFields row = {[ this.props.row[2][3], this.props.row[2][4], this.props.row[2][5] ]} /> </div>

        <div className="square"> <RowOfFields row = {[ this.props.row[0][6], this.props.row[0][7], this.props.row[0][8] ]} />
                                 <RowOfFields row = {[ this.props.row[1][6], this.props.row[1][7], this.props.row[1][8] ]} />
                                 <RowOfFields row = {[ this.props.row[2][6], this.props.row[2][7], this.props.row[2][8] ]} /> </div>


      </div>
    )
  }
}


class Sudoku extends React.Component{

  render(){

    return(
      <div className="sudoku">

        <RowOfSquares row = {[sudoku[0], sudoku[1], sudoku[2]]} />
        <RowOfSquares row = {[sudoku[3], sudoku[4], sudoku[5]]} />
        <RowOfSquares row = {[sudoku[6], sudoku[7], sudoku[8]]} />

      </div>
    )

  }
}


class Button extends React.Component{
  state = {
    text: "Solve"
  }

  render(){
    return(
      <div className="button" onClick={this.props.onClick} >{this.props.children}</div>
    )
  }
}


class App extends React.Component{
  state = {
    button: "Solve"
  }

  onClick = () => {
    console.log("click");

    if(this.state.button === "Solve"){
      this.setState({button: "Solving..."});

    }



  }

  render(){

  return (
    <div className="App">

      <Sudoku/>

      <Button onClick={this.onClick} >{this.state.button}</Button>

    </div>
  );
  }
}



export default App;
